import React from 'react';
import { useState } from 'react';

const App = () => {

const state = useState();
const[count, setcount] = useState(0);

const IncNum = () => {
    setcount(count + 1);

}
  return (
    <>
    <h1>{count}</h1>
    <button onClick={IncNum}>Click Me</button>
    </>
  );
};

export default App
